import React, { useState } from 'react'

// a proper place to define a component
const Statistics = (props) => {
  return(
    <div>
      <h2> statistics </h2>
      <Counter name="good" value={props.good}/>
      <Counter name="neutral" value={props.neutral}/>
      <Counter name="bad" value={props.bad}/>
      <Counter name="all" value={props.good + props.bad + props.neutral}/>
      <Counter name= "average" value={avg(props.good,props.bad,props.neutral)}/>
      <Counter name= "positive" value={positive(props.good,props.bad,props.neutral)}/>
    </div>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const increaseGood = () => setGood(good + 1)
  const increaseNeutral = () => setNeutral(neutral + 1)
  const increaseBad = () => setBad(bad + 1)



  return (
    <div>
      <h1> Give Feedback </h1>
      <button onClick={increaseGood}>good</button>
      <button onClick={increaseNeutral}>neutral</button>
      <button onClick={increaseBad}>Bad</button>
      <Statistics good={good} bad={bad} neutral={neutral}/>
    </div>
  )
}

const Counter = (props) => (
  <p> {props.name} {props.value}</p>
)

const avg = (good,bad,neutral) => {

  if(good + bad + neutral === 0){
    return 0
  }
  return ((good + (bad * (-1)))/(good + neutral + bad))
}

const positive = (good,bad,neutral) => {

  if(good + bad + neutral === 0){
    return 0 + "%"
  }
  return ((good/(good + neutral + bad))* 100) + "%"
}

export default App
