import React from 'react'

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'new part',
        exercises: 8,
        id: 4
      }
    ]
  }

  return (
    <Course key={course.id} course={course}/>
  )
}

const Course = ({course}) => {
  return (
    <div>
      <Header key={course.id} course={course}/>
      {course.parts.map(c => 
        <Part key={c.id} course={c}/>
      )}
      <Total key={course.id} course={course}/>
    </div>)
}

const Part = ({course}) => {
  return (
    <p>{course.name} {course.exercises}</p>
  )
}

const Header = ({course}) => {
  return (
    <h1> {course.name}</h1>
  )
}

const Total = ({course}) => {
  let total = 0
  course.parts.map(c =>
    total = total + c.exercises
  )
  return (
    <p>total of all courses {total}</p>
  )
}

export default App
