import React from 'react'

const App = () => {
    const courses = [
      {
        name: 'Half Stack application development',
        id: 1,
        parts: [
          {
            name: 'Fundamentals of React',
            exercises: 10,
            id: 1
          },
          {
            name: 'Using props to pass data',
            exercises: 7,
            id: 2
          },
          {
            name: 'State of a component',
            exercises: 14,
            id: 3
          },
          {
            name: 'Redux',
            exercises: 11,
            id: 4
          }
        ]
      }, 
      {
        name: 'Node.js',
        id: 2,
        parts: [
          {
            name: 'Routing',
            exercises: 3,
            id: 1
          },
          {
            name: 'Middlewares',
            exercises: 7,
            id: 2
          }
        ]
      }
    ]


  return (
    <Course key={course.id} course={course}/>
  )
}


const Course = ({course}) => {
  return (
    <div>
      <Header course={course}/>
      {course.parts.map(c => 
        <Part key={c.id} course={c}/>
      )}
      <Total course={course}/>
    </div>)
}

const Part = ({course}) => {
  return (
    <p>{course.name} {course.exercises}</p>
  )
}

const Header = ({course}) => {
  return (
    <h1> {course.name}</h1>
  )
}

const Total = ({course}) => {
  const total = course.parts.reduce((s,p) => s + p)
  return (
    <p>total of all courses {total}</p>
  )
}

export default App
