import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '914823' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber] = useState('')
  const [ search, setSearch] = useState('')
  const [ show, setShow] = useState(persons)


  const addPerson = (event) => {
    event.preventDefault()
    if (persons.filter(e => e.name === newName).length > 0){
      alert(`${newName} is already in phonebook`)
    } else {
      const nameObject = {
        name: newName,
        number: newNumber
      }
      setPersons(persons.concat(nameObject))
      setShow(persons)
      setNewName('')
      setNewNumber('')
    }
  }

  function findName(person) { 
    //console.log(person.name , search, person.name === search)
    return person.name === search;
  }

  const searching = (event) => {  
    event.preventDefault()
    if (search !== ''){
      let result = persons.find(findName)
      console.log(result)
      setShow(result === undefined ? [] : [result])
    } else {
      setShow(persons)
    }
    setSearch('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchChange = (event) => {
    setSearch(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter submit={searching} search={search} handler={handleSearchChange}/>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName}
          onChange={handleNameChange}
          />
        </div>
        <div>
          number: <input value={newNumber}
          onChange={handleNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
        {show.map(person => 
          <Number key={person.name} name={person.name} number={person.number}/>  
        )}
    </div>
  )
}

const Number = (props) => {
  return (
    <p>{props.name} {props.number}</p>
  )
}

const Filter = (props) => {
return (<form onSubmit={props.submit}>
          <div>
            Filter shown with <input value={props.search}
            onChange={props.handler}/>
          </div>
          <div>
            <button type="submit">search</button>
        </div>
      </form>)
}
export default App
